package eclipsetest;

public class PrintTriangularPattern {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		System.out.println("* * * * * ");
		System.out.println(" *     * ");
		System.out.println("  *  * ");
		System.out.println("   ** ");
		System.out.println("   *");

	}

}
